sections.css:
.alert-list {
    padding: 5px;
    margin: 10px 0;
    //border: 1px solid;
}

.ill-item {
  color: #9A5324;
  font-weight: 600;
}

a.title {
  font-weight: 600;
}

[id^="record"] {
	padding-top: 7px;
	padding-bottom: 7px;
	&:nth-of-type(odd) {
		background-color: #fff;
		@include respond-to(small) {
			background-color: #f2efef;
		}
	}
}

.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default {
	background-color: #806362;
}


input[type=checkbox] {
  display: none; 
  &+label {
  	font-weight: normal;
    width: 100%;
  }
}

input:checked + label {
	background-color: #dfe0d5;
	color: #616530;
	padding: 10px 0;
	margin: 0;
}

input:checked + label:before {
	content: "✓ ";
	font-size: 5em;
    position: absolute;
    right: 50px;
    z-index: 10;
    @include respond-to(small) {
    	font-size: 2em;
	    position: absolute;
	    left: 10px;
    }
}

// input:not( :checked) + label:before {
//   @include respond-to(small) {
//     content: "\039f";
//     color: #767676;
//     font-size: 1em;
//     position: absolute;
//     left: 20px;
//     padding-top: 20px;
//     z-index: 10;
//   }
// }

/* new stuff */
.check {
  visibility: hidden;
}

input:checked + label .check {
  visibility: visible;
}

input.checkbox:checked + label:before {
content: "";
}

